void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 pixel = 2./iResolution.xy;
    vec3 color = vec3(0.);
    //offset of RGB channels
    color.r=texture(iChannel0,uv+pixel.xx).r;
    color.g=texture(iChannel0,uv+pixel.xy).g;
    color.b=texture(iChannel0,uv+pixel.yy).b;
    //scanlines
    color*=length(cos(2.*fragCoord));

    fragColor = vec4(color,1.0);
}

