    float inside = smoothstep(3.24, 3.61, dot(z, z));
    vec2 uvlookup = (vec2(1.5) + z) / 3.0;
    vec3 camcolor = texture(iChannel1, uvlookup).xyz;
    vec3 color = mix (texture(iChannel0, uvlookup).xyz,
                     camcolor, 0.1);
//        inside);
    fragColor = vec4(color.xyz,1.0);
}
