void mainImage (out vec4 fragColor, in vec2 fragCoord) {
	vec2 xz = fragCoord / min(iResolution.x, iResolution.y) * 2. - 1.;
    float PI = 3.1415926538;
    float x = xz.x, z = xz.y;
    float y = sqrt(1.-x*x-z*z);
    if (y != y) {
        fragColor = vec4(0,0,0,0);
        return;
    }

    float lon1 = atan(y,x);
    float lat1 = acos(z);

    float f = iTime * 2. + texture(iChannel0,vec2(lon1-iTime*0.5,lat1)).x*1.6;

    float x2 = x*sin(f)+y*cos(f);
    float y2 = x*cos(f)-y*sin(f);
    x=x2; y=y2;

    float lon = atan(y,x);
    float lat = acos(z);
    fragColor = vec4(lat/PI,lon/PI,0,0);
}
