vec2 rotate(float ang, vec2 uv){
    // Rotate the given coordinate to given angle
    // float ang: angle
    // vec2 uv: pixel coordinates

	float sine = sin(ang);
    float cosine = cos(ang);
    uv = (uv - .5) * mat2(cosine, sine, -sine, cosine); // Matrices
    uv += .5; // Translate the coordinates back
    return uv;
}
