void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
    uv.x+=1.5;  
    uv.y += cos(uv.x*10.0)*0.15;
    uv.y*=1.5;
    float r = 7.3,
    e = uv.x-iTime*0.5,
    t = e + abs(uv.y),
   //s = step( fract(t*r),f);
   // s = smoothstep(1.5,0., (fract(t*r)-f)/fwidth(t*r-f)); 
   //O = vec4(s);
   f = (1.0+sin(uv.x*3.0))*0.4+0.1, 
   d = f - abs(fract(t*r) - .5)*2.,
   s = smoothstep(0., .05/max(d + .5, 0.), d + .1);    
   O = vec4(sqrt(s));
}
