#define PI 3.1415
#define width .5
#define height .125
#define threshold (PI*.75)

#define P vec2( sin(mod(p, threshold) * 4.) * s, cos(mod(p, threshold) * 4.) * s) * height + vec2(width, 0.) * s
#define L vec2( mix(-.5, .5, pow(p/threshold, .33)), height) * s

float circle(const in vec2 U, const in float t, const in float aa){
	float s = -(mod(floor(t/PI), 2.) * 2. - 1.),
	p = mod(t, PI),
	lr = 1. - step(threshold, p);
	float size = sin(t * 2.) * .075 + .09;
	return smoothstep(size + aa, size, distance(mix(P, L, lr), U));
}

#define mainImage(O,u)                      	      \
	O = vec4(vec3(0.), 1.);						      \
	float t = iTime * 3.;							  \
	vec2 R = iResolution.xy,		                  \
	U = ( u+u - R )/R.y; 							  \
    float aa = 8./R.y;								  \
    O.xyz += circle(U, t, aa);					  	  \
    O.xyz += circle(U, t - PI/4., aa);				  \
    O.xyz += circle(U, t - PI/2., aa);
