void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col=vec3(.0);

    float off=iTime*.12;
    float amp=sin(uv.x*5.);
    float amp2=sin(uv.x*1.);

    col.x=pow(1.-distance(vec2(uv.x, (sin((uv.x+off)*40.)*.25*amp-uv.y+0.5)*amp2), uv),3.);

    //if(col.x>.9) 
    col.y=pow(1.-distance(uv,vec2(.0)),2.);


	fragColor = vec4(col,1.0);
}
