/**
 * My chrome extension for Shadertoy:
 * 
 * http://bit.ly/shadertoy-plugin 
 */

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

void mainImage(out vec4 C, in vec2 F) {
	vec2 
        r = iResolution.xy,
        u = F / r - .5;

    float t = iTime;

    u.x *= r.x / r.y;
    pR(u, -t * 5. + (sin(t) * 3.+ 1.));

    C = vec4(length(u) < .2 && length(u) > .1 ? 1.- atan(u.y, u.x) * .5 : 0.);

}
