//Nice colors
vec3 rgb(vec3 col)  {
    return vec3(col.r / 255., col.g / 255., col.b / 255.);
}

float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
  vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix(mix(rand(b), rand(b + d.yx), f.x), 
               mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float circle(vec2 r, float radius, vec2 center) {
    return smoothstep(radius - .002, radius + .002, length(r - center));    
}

float wavyCircle(vec2 r, float radius, vec2 center, float waviness) {
    vec2 r2 = center - r;
    r2*=1.+waviness*(noise(r * 20. * sin(iTime)) - .5);
    return circle(center + r2, radius, center);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;
    float maxX = iResolution.x / iResolution.y;
    vec3 background_color = rgb(vec3(sin(iTime * 6.)*255., 133, 27));
    vec3 circle_color = rgb(vec3(0, 31, 63));
    float circle_alpha = wavyCircle(uv , .3, vec2(.5 * maxX, .5), .14);
    vec3 pixel = mix(circle_color,background_color, circle_alpha);
    if ((circle_alpha > 0.0) && (circle_alpha < 1.0)) {
        float new_alpha = -10.*abs(circle_alpha - .5) + 5.;
        new_alpha = clamp(new_alpha, 0.0, 1.0);
    	pixel = mix(pixel, rgb(vec3(1, 255, 112)), new_alpha);
    }
	fragColor = vec4(pixel, 1.0);
}
