//this plot shows how it works:
//http://fooplot.com/#-
const float slices = 6.;
const float vel = 0.1;
#define half_pi 1.570796

float rectangle(vec2 uv, vec2 pos, float width, float height) {
	return step(pos.x, uv.x) * step(uv.x, pos.x + width) * step(pos.y, uv.y) * step(uv.y, pos.y + height);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float index = slices - floor(slices * uv.y);
    uv.y *= slices;

    //mirror
    if(step(uv.x, 0.5)==0.) uv.x = 1.-uv.x; 
    uv.x = fract(uv.x*2.);

    //binary progression
    float freq = pow(2., index-1.) * iTime * -vel;
    //*
    float osc = freq;
    for(float i=0.;i<slices;i++){
        if(slices-i<index-1.) break;
        osc = sin(sin(osc)*half_pi)*half_pi;//square curve
    }
    osc = sin(osc);
	/**/
    //simplier version
    //float osc = sin(sin(sin(sin(sin(freq)*half_pi)*half_pi)*half_pi)*half_pi);//square curve
    osc = (osc+1.)/2.;

    vec2 pos = vec2(0.5, 0.15);
    float width = 0.9+0.1*osc;
    float height = 0.7;

    float rect = 1.-rectangle(fract(uv), pos, width-pos.x, height);

    fragColor = vec4(vec3(rect),1.0);
}
