vec2 getsoundimage(float time,float x,float y)

{  
    float tau=acos(-1.)*2.;
    if ((y<.4)&&(y>.1)){time+=x/4.;};
    if ((y>.6)&&(y<.9)){ time+=x*4.;};

    time-=.5;
    float tim=fract(time/8.)*8.;
    float t=tim+.5;

    vec2 sum= vec2( sin(tau*(468.0)*tim)*exp(-3.0*tim) );
        sum+= float(t>0.)*vec2( sin(tau*(432.0+-15.*cos(tim*10.))*t)*exp(-1.0*t) );
      t-=.5;
         sum+= float(t>0.)*vec2( sin(tau*(216.0)*t)*exp(-3.0*t) );
     t-=.5;
         sum+= float(t>0.)*vec2( sin(tau*(432.0)*t)*exp(-3.0*t) );
     t-=.25;
         sum+= float(t>0.)*vec2( sin(tau*(864.0)*t)*exp(-3.0*t) );

    t-=.25;
         sum+= float(t>0.)*vec2( sin(tau*(468.0)*t)*exp(-3.0*t) );
    t-=.25;
         sum+= float(t>0.)*vec2( sin(tau*(234.0)*t)*exp(-3.0*t) );



    return 1.-vec2(distance(y,.5+.5*sum.y));}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);
    fragColor=vec4(getsoundimage(iTime,uv.x,uv.y),0,0);
}

