#define BRIGHTNESS 2.0

float A = 0.22; 		  // Shoulder Strength
float B = 0.30; 		  // Linear Strength
float C = 0.10; 		  // Linear Angle
float D = 0.20; 		  // Toe Strength
float E = 0.01; 		  // Toe Numerator
float F = 0.30; 		  // Toe Denominator	  
float gamma = 2.2;		  // Gamma

vec3 tonemap(vec3 col){

	col *= BRIGHTNESS;
	col = ((col * (A * col + C * B) + D * E) / (col * (A * col + B) + D * F)) - E/F;
	return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);
    vec4 whitepoint = texture(iChannel0, vec2(0.5), 9.0);

    // Filmic tonemapping formula
    vec3 color = tonemap(vec3(col))/tonemap(vec3(whitepoint));

    // Output to screen
    color = pow(color, vec3(1.0/gamma));
    fragColor = vec4(color, 1.0);
}
