#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv*2.0-1.0;
    p.x *= iResolution.x/iResolution.y;

    float speed = iMouse.x/iResolution.x;
    float zoom =  0.9;
    float radius = 0.75;

    float weight = 25.9;

    const int nstars = 12;
    float stars[nstars];

    int i = 0;
    while(i < nstars) {
        float r = 0.05;
        float theta = (float(i)/float(nstars)+fract(speed*iTime))*2.0*PI;
    	vec2 pt = zoom*p-radius*vec2(sin(theta),-cos(theta));
    	float a = atan(pt.y, pt.x);
    	float d = (length(pt)-r)-0.25*r*sin((a-theta)*5.0);

    	stars[i++] = d;
    }

    float d = 100.0;
    i = 0;
    while(i < nstars) {
        d = min(stars[i++], d);
    }

    float a = 1.0-abs(weight*d);
    vec3 col = vec3(1,0,0);

    // Output to screen
    fragColor = vec4(a*col,1.0);
}
