//This shader gives a nice 2d stream effect 
//and makes the stars twinkle a bit in the background
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv = iMouse.xy/iResolution.xy;
    if(muv.y == 0.0)
        muv.y = 0.10;
  	muv.y = muv.y + cos(10.0*iTime) * 0.0001;

    if(uv.y < muv.y){
        vec4 color_bl = vec4(0.0,0.0,0.0,0.0);
        vec4 color_wt = color_bl + 1.0;

        float w = muv.y + cos(iTime) * 0.01;

        uv.y = ((w - uv.y)/w)*(1.0-w)+w ;
        vec2 n = vec2(uv.x,uv.y);
        n.x += iTime * 0.10;
        n.y += iTime * 0.02;
        uv += texture(iChannel3,n).rg;


    	fragColor = texture(iChannel0, uv);

    }
    else{
        vec2 n = vec2(uv.x,uv.y);
        n.x += cos(0.75*iTime) * 0.02;
        n.y += cos(0.25*iTime) * 0.01;
        uv += texture(iChannel2,n).rg * 0.005;
    	fragColor = texture(iChannel0, uv);
    }
}
