// parameters
vec2 ImageCircleScale = vec2(1.8, 1.2);
vec2 ImageCircleCenter = vec2(0.5, 0.5);
vec3 VignettingColor = vec3(0.0, 0.0, 0.0);
float VignettingIntensity = 1.0;
float NaturalVignettingScale = 1.0;
float NaturalVignettingCosPower = 1.0;
float PenumbraScale = 1.0;
float RotationAngle = 0.0;

#define saturate(x) clamp(x, 0.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0, 0, 0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // aspect ratio
    float aspect_ratio = iResolution.x / iResolution.y;

    // radius
#if 0
    vec2 d = abs(uv - ImageCircleCenter) *  vec2(2.0 * aspect_ratio, 2.0) * (vec2(1.0) / ImageCircleScale);
    float r2 = dot(d, d);
    float optical_vignetting = r2;
#else
    vec2 d = abs(uv - ImageCircleCenter);
    d *= vec2(2.0 * aspect_ratio, 2.0) * (vec2(1.0) / ImageCircleScale);
    float r2 = dot(d, d);
    float optical_vignetting = r2;
#endif

    // TODO: natural vinetting

    // vignetting rate
    float vignetting_rate = saturate(pow(optical_vignetting, PenumbraScale) * VignettingIntensity);

    // scene color
    vec4 scene_color = texture(iChannel0, uv);

    // Victorian London
    // from Dave_Hoskins
    scene_color = (scene_color.x+scene_color.y+scene_color.z) * vec4(.5, .4, .2, 1);

    // final color
    col = mix(scene_color.rgb, VignettingColor, vignetting_rate);

    // Output to screen
    fragColor = vec4(col,1.0);
}
