void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v = 2.0 * (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float radius = length(v);
	float theta = atan(v.y, v.x);
   	float s = (cos(iTime) + 2.0)/3.0 * sin(theta*7.0 + 2.0*iTime);
    float flower = smoothstep(s - 0.01, s + 0.01, radius);

    vec3 col = flower * vec3(0.1,0.5,0.1) + (1.0 - flower) * vec3(1,0,0);

    fragColor = vec4(col,1.0);
}
