void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy; //0 to 1
    uv -= 0.5; //-0.5 to 0.5
    uv.x *= iResolution.x / iResolution.y; 
    //multiply x axis by the aspect ratio of the screen
    //x is bigger than y, so x/y will be greater than 1.
    //so in the x direction there will be more pixels.
    //now the pixels in x and pixels in y are one for one

    float d = length(uv);

    float c = d;
    if(d < 0.3) c = 1.0; else c = 0.0;

    fragColor = vec4(vec3(c), 1.0);
}
