#define SCALE 25.
#define SPEED 5.
#define FREQUENCY .7

float d;
#define D(p,o)  ( d=length(p-o)<=.1 ? d:.95 )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y; 
   	uv *= SCALE;

    vec2 f = fract(uv);
    uv = floor(uv);

    float t= sqrt( uv.x*uv.x + uv.y*uv.y) * FREQUENCY + iTime*SPEED;

    vec2 o = vec2(cos(t),sin(t))*.4+.5;

    fragColor.xyz = vec3(D(f,o));
}
