void mainImage(out vec4 O,vec2 u){
	vec2 R = iResolution.xy, U = u/R;
	U = 100. * vec2(U.x*1.2 - .1, abs(U.y - .5));
	O = texture(iChannel0, vec2(floor(U.x) * .01, 0.)) * 15. + .5;
	O = vec4(max(smoothstep(.1, .2 + 120./R.x,
                            length(vec2(fract(U.x) - .5, U.y - O.x*min(U.y/O.x, 1.)))),
                 step(50., abs(U.x - 50.))));}
/**/
