#define N 6
vec2[N] c;

float avgDistance(vec2 uv)
{
 	float d = 0.0;
    float k = 30.+sin(iTime)+cos(iTime);
    for(int i=0; i<N; i++)    
     	d+= cos(k*distance(uv,c[i])); 
    return d/float(N);  
}

vec4 distToColor(float d)
{
    return vec4(.95,1.2-sin(d),cos(d),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy * .5) / iResolution.y;
    for(int i=0; i<N; i++)
    {
     float fi = 2.0*3.14*float(i)/float(N);
     c[i]= vec2(sin(fi), cos(fi))*(cos(iTime)+2.)*.1;
    }
	fragColor = distToColor(avgDistance(uv));
}
