float f(vec2 a, vec2 b) {
    return dot(a, b) * exp(-a.x*a.x -a.y*a.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy - iResolution.xy*.5) / max(iResolution.x, iResolution.y);
    fragColor = vec4(0);
    uv *= 10.0;
	float c = 0.0;
    for(float a = 0.0; a<=3.0; a+=0.05) {
	    vec4 t = texture(iChannel0, vec2(sin(a), cos(a)) * iTime * 0.005 * a);
        vec3 color = mix(vec3(1,0.5,0), vec3(0,0.5,1), a/3.0);
    	fragColor.rgb += color * f(uv - (t.ba*2.0-1.0), t.rg*2.0-1.0);
    }
    fragColor += 0.821345;
}
