vec4 contrast(vec4 colors, float contrast)
{
  colors.rgb /= colors.a;
  colors.rgb = ((colors.rgb - 0.5) * max(contrast + 1.0, 0.0)) + 0.5;
  colors.rgb *= colors.a;
  return colors;
}

vec4 brightness(vec4 colors, float brightness)
{
  colors.rgb /= colors.a;
  colors.rgb += brightness;
  colors.rgb *= colors.a;
  return colors;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / -iResolution.xy;
	vec4 colors = texture(iChannel0,uv);
	float ratioBrigthness = sin(iTime*0.5)*0.5;
	float ratioContrast = sin(iTime*0.25)*0.5;
	fragColor = brightness(contrast(colors,ratioContrast),ratioBrigthness);
}
