vec3 wood_color = vec3(0.4, 0.3, 0.2);
vec3 dark_wood_color = vec3( 0.2, 0.15, 0.1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // offset centers rings
	vec2 offset = vec2(-iResolution.x/2., -iResolution.y/2.);
    vec2 pos = fragCoord.xy + offset;

	float r = length(pos);


    // eccentricity:
    r *= iTime;


    float density = sin(r);

    vec3 outcolor = vec3(mix(
        wood_color,
        dark_wood_color,
        density)
    );

	fragColor = vec4(outcolor,1.0);
}
