float usin(float x){return .5+.5*sin(x);}
float ucos(float x){return .5+.5*cos(x);}
vec2 mv(float x) {
 return vec2(sin(x + iTime)+4.*cos(x+iTime), cos(x + iTime)+2.*sin(iTime));	
}

vec4 vn(vec2 uv) {
	vec4 col = vec4(0.);
	vec2 pt;
	float stp = .2;
	vec2 ptp = vec2(stp);
	float c = 0.1;
	float m = 3.;
	float d;
	float e;
	vec2 o;
	float t = iTime;
	for(int i = 0; i < 50; i++) {
		ptp.x = mod(ptp.x + stp, iResolution.x/iResolution.y);
		ptp.y = mod(ptp.y + stp, 1.);
		pt = ptp + mv(c)*.1;
		c += stp;

		o = uv-pt;
		float p = 2.5;
		// wonky metric
		d = length(o) + .1*ucos(atan(o.y, o.x))*sign(o.y);
		// Lp metric
		//d = pow(pow(o.x, p)+pow(o.y, p), 1./p);
		// manhatten
		//d = abs(o.x)+abs(o.y);
		//euclidean
		//d = length(o);
		if (d < m) {
			e = ucos(abs(d-m)*25.);
			m = d;
			col = vec4(usin(c)-e, ucos(c*e), usin(e+c), 1.);
		}
	}
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 col = vn(uv);
	fragColor = col;
}
