void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 src = texture(iChannel0, fragCoord / iResolution.xy).rgb;
    float lum = texture(iChannel1, fragCoord / vec2(8)).r;
    fragColor = vec4(src.r >= lum ? 1.:0.,
                     src.g >= lum ? 1.:0.,
                     src.b >= lum ? 1.:0.,
                     1.); 
}
