vec4 drawArrow(vec2 pixel, vec2 origin, vec2 arrow, float head) {
    vec2 p = pixel - origin;
    vec2 v = normalize(arrow);
    float projection = dot(p, v);
    if (projection < 0.0) {
        return vec4(0, 0, 0, 1);
    }
    vec2 offset = p - projection * v;
    float body = max(0.0, length(arrow) - head);
    float l = length(p);
    float w = 0.0;
    if (l < body) {
        w = 2.0;
    } else if (l < body + head) {
    	w = body + head - l;
    }
    if (length(offset) < w) {
    	return vec4(1, 1, 1, 1);
    } else {
    	return vec4(0, 0, 0, 1);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = drawArrow(fragCoord.xy, iResolution.xy / 2.0, iMouse.xy - iResolution.xy / 2.0, 10.0);
}
