float field(in vec3 p) 
{
	float accum = 0.;
	float prev = 0.;
	float tw = 0.;
    vec3 growing = vec3(iTime*0.07 + 0.47, iTime*0.07 + 0.47, iTime*0.07 + 0.5);
    growing += sin(iTime * .0476) * .05;
	growing = abs(sin(growing)) * .05 + .45;
    for (int i = 0; i < 20; ++i) 
    {
		float mag = pow(length(p), 2.0);
        float magDelta = abs(mag - prev);

		p = abs(p) / mag - growing;
		float w = pow(10.0, -float(i) / 100.0);
		accum += w / pow(10.0, magDelta*2.0);
		tw += w;
		prev = mag;
	}

	return max(0., 2.5 * accum / tw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y) / 2.0;
	vec3 p = vec3(uvs.x + 0.5, uvs.y + 0.5, 0.0);
	float t = field(p);
    vec4 c0 = vec4(0.0, 0.0, 0.05, 1.0);
    vec4 c1 = vec4(.13, 0.28, 0.28, 1.0);
    vec4 c2 = vec4(0.0, .5, 2, 1.0);
    vec4 c3 = vec4(1.0, 2, .5, 1.0);
	fragColor = mix(c0, mix(c1, mix(c2, c3, 1.0+cos(uv.x*uv.y*6.0 + p.y)), max(t - 0.5, 0.0)*2.0), max(t * 5.0, 0.0));
	fragColor = normalize(fragColor) * .5 + fragColor * .5;
}
