void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 pos = vec2(0.0, 0.0); 										//circle position x, y

    float dist = length(uv-pos);
    float c = sin(iTime*1.5+smoothstep(0.3, dist, 0.32));		//external line
    float c1 = sin(iTime*1.5+smoothstep(0.32, dist, 0.3));	//inner line

    float in_c = smoothstep(0.0, dist, 0.3);						//inner cirlce
    float out_c = smoothstep(0.3, dist, 1.0);						//external circle(bg)

    //inner circle color
    vec4 col1 = vec4(sin(abs(iTime)), cos(abs(iTime)), sin(abs(iTime))*cos(abs(iTime)), 1.0)*in_c;

    //external circle color(bg) static
    vec4 col2 = vec4(0.0, 0.0, 0.0, 1.0);
    //external circle color(bg) animated
    //vec4 col2 = vec4(-sin(abs(iTime)), -cos(abs(iTime)), -sin(abs(iTime))*cos(abs(iTime)), 1.0)*out_c;

    //line color
	vec4 col_c = vec4(cos(abs(iTime)), sin(abs(iTime)), tan(iTime), 1.0)*c+c1;

	fragColor = vec4(col_c+col1+col2);
}
