// inspired from   https://www.shadertoy.com/view/4sSXWR

void mainImage( out vec4 O, vec2 U ){

    vec2 R = iResolution.xy;
    O = texture(iChannel0,U/R);  // x = path, y = maze

    O = mix( vec4(1.-O.y), vec4(1,0,0,0), O.x)
        -.5*smoothstep(.1,.08,4.*length(U/R.y - vec2(.8,.5)))*sin(20.*iDate.w); // cursor   
}

// inspired from   https://www.shadertoy.com/view/4sSXWR

#define rnd( x)    fract(1000.*sin(345.2345*x))
#define id( x,y)   floor(x)+100.*floor(y)

float maze(vec2 u) {
    float n = id(u.x,u.y);  u = fract(u);
    return 1.-smoothstep(.1,.15,((rnd(n)>.5)?u.x:u.y));  // .1 .15
}

float keyPress(int ascii) {
    //if (iResolution.y<2000.) return step(.99,rnd(t+float(ascii)));
	return texture(iChannel2,vec2((.5+float(ascii))/256.,0.25)).x ;
}

void mainImage( out vec4 O, vec2 U )
{
    float t = iTime;
    // --- update state from commands + past
    vec2 R = iResolution.xy;
    float  x = texture(iChannel0, .5/R).a, // state variable : position, direction
           y = texture(iChannel0,1.5/R).a,
          dx = texture(iChannel0,2.5/R).a,
          dy = texture(iChannel0,3.5/R).a,
          left = keyPress(37)+(iMouse.y==0.?1.:0.), right = keyPress(39), // keys
            up = keyPress(38),  down = keyPress(40),
          stop = keyPress(32),
           key = left+right+up+down+stop;
    O.a = texture(iChannel0, U/R).a;    

    if (maze( ( vec2(.8,.5)*R.y + vec2(x,y-15.)+10.*vec2(dx,dy) )* 8./R.y ) > 0.) 
             dx=dy=0.; // collision

    if (U.x==U.y) {  // new direction, position
 		if      (U.x==2.5 && key>0.)  O.a = right-left;
        else if (U.x==3.5 && key>0.)  O.a =    up-down;
    	else if (U.x== .5)  O.a = x+dx;
    	else if (U.x==1.5)  O.a = y+dy;
     }

    // --- draw path and maze
    vec2 U_ = 1.-step(R-1.,abs(U+U-R));
    O.x = texture(iChannel0, (U+vec2(dx,dy))/R).x * U_.x*U_.y // move path
         + smoothstep(.1,.08,4.*length(U/R.y - vec2(.8,.5)));   // + cur pos
    O.y  =  maze( ( U + vec2(x,y-15.) )* 8./R.y );     
}
