void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float EPSILON = 0.005;

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 foci[3];
    foci[0] = iMouse.xy / iResolution.xy;
    foci[1] = vec2(0.4, 0.5);
    foci[2] = vec2(0.6, 0.5);

    fragColor = vec4(0., 0., 0., 1.);

    float totalDistance = 0.;
    for(int i = 0; i < 3; i++) {
        float currentDistance = length(uv - foci[i]);
        if (currentDistance < EPSILON) {
            fragColor = vec4(0., 0., 1., 1.);
        }

        totalDistance += currentDistance;
    }

    float R = 0.5;

    float intensity = 1.;


    fragColor += .003 / abs(totalDistance - R);

}
