const float PI = 3.14159265359;


vec3 spiral(in vec2 uv, in vec2 pos, in float rev, in float freq)
{
    uv = uv - pos;
    float angle = atan(uv.x, uv.y);
    float rad = length(uv) * PI * rev;
    float r = cos(angle + rad - iTime * 1.0 * freq) / PI + 0.5;
    float g = cos(angle + rad - iTime * 2.0 * freq) / PI + 0.5;
    float b = cos(angle + rad - iTime * 3.0 * freq) / PI + 0.5;

    return vec3(r, g, b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.yy;

    float t = iTime * 1.0;
    float cost = cos(t);
    float sint = sin(t);

    mat2 m = mat2(cost, sint, -sint, cost);

    uv *= m;

    t = sin(iTime * 0.5) + PI * 0.5;
    fragColor = vec4(spiral(uv, vec2(-0.0, 0.0) * t, 50.0 + t * 50.0, 4.0), 1.0) * 1.0;
   fragColor += vec4(spiral(uv, vec2(0.25, 0.0) * t, 6.0 + t * 6.0, 5.0), 1.0) * 1.0;
    fragColor *= vec4(spiral(uv, vec2(0.0, 0.25) * t, 6.0 + t * 6.0, 6.0), 1.0) * 1.0;
    fragColor *= vec4(spiral(uv, vec2(0.0, -0.25) * t, 6.0 + t * 6.0, 7.0), 1.0) * 1.0;
