void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 bl = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 br = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 tl = vec4(0.0, 0.0, 1.0, 1.0);
    vec4 tr = vec4(0.0, 1.0, 1.0, 1.0);
    fragColor = mix(
        mix(bl, br, uv.x),
        mix(tl, tr, uv.x),
        uv.y
    );
}
