/**  // 139 chars 1-tweet version

#define mainImage(O,u)                              \
    vec2 R  = iResolution.xy, U = u+u-R;            \
    float l = length(U)/R.y;                        \
    O += .5 + sin( 3. * atan(U.y,U.x) + 12.*iTime   \
                         + l* (   l < .7  ?  60.    \
                                : l < 1.4 ? -45.    \
                                :            36. ) )
/**/        

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = (U+U-R)/R.y;
    float t = 4.*iTime, l = length(U), a = atan(U.y,U.x);
    O = vec4(.5 + sin(  l < .7  ? 3.*(a+20.*l+t)
                      : l < 1.4 ? 3.*(a-15.*l+t)
                      :           3.*(a+12.*l+t) ) );
}
