void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 50. * fragCoord.xy / iResolution.xy - 25. ;
    uv.x *= iResolution.x / iResolution.y;
    uv *= pow(1. / length(uv), 2.);
    float size = .5 * (1. + sin(iTime));
    vec3 col ;
	float Time = mod(iTime, 3.0);
    if (Time < 1.0)
        col = vec3(fract(Time), 0.5, 0.9);
    else if (Time < 2.0)
        col = vec3(0.7, fract(Time), 0.3);
    else if (Time < 3.0)
        col = vec3(0.1, 0.4, fract(Time));
    if (uv.x <= -size || uv.x >= size || uv.y <= -size || uv.y >= size)
    	fragColor.xyz = col / 1.5;
    else
        fragColor.xyz = col;

}
