float whoAmI(in vec2 p)
{
    vec2 ap = abs(p);
    float tmp = 3. * sqrt(33.);
    float s = pow(p.x / 7., 2.) * sqrt(abs(ap.x - 3.) / (ap.x - 3.));
	float tmp1 = p.y + tmp / 7.;
    s +=  pow(p.y / 3., 2.) * sqrt(abs(tmp1) / tmp1) - 1.;
    float s1 = abs(p.x / 2.) - ((tmp - 7.) / 112.) * pow(p.x, 2.) - 3. + sqrt(1. - pow((abs(ap.x - 2.) - 1.), 2.)) - p.y;
    float tmp2 = ap.x - .75;
    float tmp3 = ap.x - .5;
    float s2 = -(9. * sqrt(abs((ap.x - 1.) * tmp2) / ((1. - ap.x) * tmp2)) - 8. * ap.x - p.y);
    float s3 = 3. * ap.x + .75 * sqrt(abs(tmp2 * tmp3) / ((.75 - ap.x) * tmp3)) - p.y;
    tmp2 = p.x + .5;
    float s4 = 2.25 * sqrt(abs((p.x - .5) * tmp2) / ((.5 - p.x) * tmp2)) - p.y;
    tmp2 = ap.x - 1.;
    float s5 = 6. * sqrt(10.) / 7. + (1.5 - .5 * ap.x) * sqrt(abs(tmp2) / tmp2) - ((6. * sqrt(10.) / 14.) * sqrt(4. - pow(ap.x - 1., 2.)) + p.y);
    return s * s1 * s2 * s3 * s4 * s5;
}


