#define TAU 6.28318530718

vec4 blend(vec4 c1, vec4 c2) {
    return vec4(mix(c1.rgb, c2.rgb, c2.a), max(c1.a, c2.a)); 
}

// antialiasing trick as suggested by Fabrice
float aa(float value, float degree) {
    return clamp(20. * degree * degree * sin((value + 1.) * TAU / 2.), 0., 1.);
}

vec4 shape(vec2 uv, float offset, float points) {
    uv.y = -uv.y;
    float l = length(uv);
    vec2 tube = vec2(
        fract((atan(uv.x, uv.y) / TAU + .5) * points),
        1. / l);
    tube.y /= cos((tube.x - .5) * TAU / points);
    tube.y += offset;
    float dist = (tube.y - offset) / 10.;
	return vec4(vec3(aa(tube.y * 3., l) + smoothstep(0.1, .8,dist)), float(mod(tube.y, 20.) < 9.));
}

vec4 tube(vec2 uv, float offset) {
    float l = length(uv);
	return vec4(vec3(aa(4. / l + offset, l) + 1. - smoothstep(0.2, .5, l)), 1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 R = iResolution.xy,
	uv = (2.*fragCoord - R) / R.y;

    float offset = iTime - 10.;

    // hash the number of points in the shape of the next sequence
    float numPoints = 3. + floor(mod(sin(floor((offset + 10.) / 20.) * 123.456) * 123., 4.));

    vec4 c0 = tube(uv, offset * 2.);
    vec4 c1 = shape(uv, -offset, numPoints);

    fragColor = blend(c0, c1);
}
