void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

vec2 dfr(in float a) {
    return normalize(vec2(sin(a+iTime+cos((a+iTime)*3.14)),
                          cos(a+iTime+sin((a+iTime)*3.14))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 10) {fragColor = max(texture(iChannel1,fragCoord/iResolution.xy)-.6,0.)/.4;return;}

    vec2 uv = fragCoord/iResolution.xy;

    if (iMouse.w > 0. && length(uv-iMouse.xy/iResolution.xy) < .1) {fragColor = vec4(0.);return;}


    vec2 odr = 1./iResolution.xy;
    vec4 s0 = texture(iChannel0, uv+vec2(odr.x,0.)),
         s1 = texture(iChannel0, uv+vec2(0.,odr.y)),
         s2 = texture(iChannel0, uv-vec2(odr.x,0.)),
         s3 = texture(iChannel0, uv-vec2(0.,odr.y));
    fragColor = (s0*dot(vec2(-1.,0.),dfr(s0.x))+
        		 s1.wxyz*dot(vec2(0.,-1.),dfr(s1.y))+
        		 s2.zwxy*dot(vec2(1.,0.),dfr(s2.z))+
        		 s3.yzwx*dot(vec2(0.,1.),dfr(s3.w)))/2.25;
}

vec4 fix(in vec4 v) {
    if (length(v) == 0.) return vec4(0.);
    //return normalize(v);

    vec4 av = abs(v);
    return av/max(av.x,max(av.y,max(av.z,av.w)));
    return vec4(max(av.x,max(av.y,max(av.z,av.w))));    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = (texture(iChannel1, uv)*.9+
        		fix(texture(iChannel0, uv))*.1);
}
