void mainImage( out vec4 f, vec2 g )
{
	f = texture(iChannel0, g / iResolution.xy);
}

void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
	vec2 v2 = g / f.xy;
    vec2 v3 = (g+g-f.xy)/f.y*8.;

    float t = iTime*.5;

    v3 = vec2(atan(v3.x,v3.y)/3.14159*5., length(v3));
    v3.x *= floor(v3.y);
    v3.x += floor(v3.y) * t;
   	v3 = abs(fract(v3)-0.5);

    vec4 buf = vec4(0.01/dot(v3,v3));

    if (iMouse.z>0.)
    	v2 *= 0.999;
    else
        v2 *= (.99 + vec2(cos(t*t),sin(t*t)) * 0.005);

    vec4 tex = texture(iChannel0, v2);

    f = buf * 0.1 + tex * 0.9;
}
