vec3 frame(float border, vec2 tc, vec3 col){

    tc = -1.0 + 2.0 * tc;
    float m = max(abs(tc.x), abs(tc.y));
    m = smoothstep(1.0 - border, 1.0 - border +0.01, m);

    return m * col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float borderWidth = 0.2;
    vec3 frameColor = vec3(uv.x, uv.y, 0.5);
    vec3 f = frame(borderWidth, uv, frameColor );

    fragColor = vec4(f,1.0);
}
