void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    // ring
    float u = abs(sin((atan(p.y, p.x) + iTime * 0.5 + length(p) ) * 15.0)*0.5) + 0.25;
    float t = 0.2 / abs(0.35*sin(iTime) + u - length(p)); 
    vec4 tex = texture(iChannel0, vec2(uv.y-iTime, -uv.x));
    fragColor = vec4(tex.x-t, tex.y-t, tex.z-t, 0.5);
}
