void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  	vec2 xy = fragCoord / iResolution.xy;

    float t = fract(iTime);

    if (t < 0.5)
        t *= 2.0;
   	else
        t = 1.0 - (t - 0.5) * 2.0;

    xy.x = (xy.x - 0.5) * t + 0.5;
    xy.y = (xy.y - 0.5) * (1.0 - t) + 0.5;

	fragColor = texture(iChannel0, xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 xy = (fragCoord / iResolution.x) - vec2(.5, .28);
  	float len = length (xy);

	if (len < 0.1)
		fragColor = vec4(0, 0, log((1.0-len/0.1)*5.0), 1);
   	else
    	fragColor = vec4(0, 0, 0, 1);
}
