void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 o = vec2(0.5, 0.5);
    vec2 oo = vec2(iMouse)/iResolution.xy;

    float a,b,c;
    a = 0.5+0.5*cos(32.0*uv.x-1.0*cos(15.0*uv.y));
    b = 0.5+0.5*cos(32.0*uv.x+1.0*cos(15.0*uv.y));
    if (oo.x > 0.5) {
    	c = a;
    } else {
    	c = b;
    }
    //c = 0.5+0.5*(a-b);

    vec3 col1 = vec3(c, 1.0-c, c);

    if (length(uv-o) < 0.0025) {
        col1 = vec3(0.0, 0.0, 0.0);
    }

    fragColor = vec4(col1,1.0);
}
