/*----------------------------------------------------------
 LODTest.glsl

 Testing 'Level Of Detail' feature on textures 

 v1.0 2015-11-18
 v2.0 2018-07-29

 tags:  2d, test, textures, lod, levelofdetail

----------------------------------------------------------*/

#define MAX_LOD 6.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float lod = 2.5 + 2.5*sin(iTime);
    vec3 col = textureLod( iChannel0, uv, lod).xyz;
    if (iMouse.z > 0.5)
        col = textureLod( iChannel0, uv, MAX_LOD*iMouse.x/iResolution.x).xyz;
	fragColor = vec4(col,1.0);
}
