float verticalLines(vec2 fragCoord, float thickness, float gutter) {
    return step(gutter, mod(fragCoord.x, thickness + gutter));
}

float curve(vec2 fragCoord, float range, float height) {
    return step(fragCoord.y, iResolution.y * 0.5 + range * range * height + height * 0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   float lines = verticalLines(fragCoord, 2.0, 5.0);

   float inclineRange = clamp(((fragCoord.x - mod(fragCoord.x, 7.0)) - iResolution.x * 0.4) / (iResolution.x * 0.1), 0.0, 1.0);
   float declineRange = 1.0 - clamp(((fragCoord.x - mod(fragCoord.x, 7.0)) - iResolution.x * 0.5) / (iResolution.x * 0.1), 0.0, 1.0);

   float topCurve = curve(fragCoord, inclineRange, 20.0) * curve(fragCoord, declineRange, 20.0);
   float bottomCurve = (1.0 - curve(fragCoord, inclineRange, -20.0)) * (1.0 - curve(fragCoord, declineRange, -20.0));

   float intersection = topCurve * bottomCurve * lines;
   fragColor = vec4(vec3(intersection), 1.0);

}
