vec4 sky = vec4(0.3, 0.3, 0.9, 1.0);

float xRes = float(32);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.x;

    //uv.y /= iResolution.x/iResolution.y;

    xRes = 128.0 * iMouse.x/iResolution.x + 16.0;

    uv *= xRes;
    uv = floor(uv);

    uv /= xRes;

    float timeScale = 0.5;

    vec2 pos = vec2(sin(iTime * timeScale)/2.0, 0.0);

    float dist = (cos(iTime * timeScale) + 2.0) / 10.0;

    float blurness = iMouse.y/iResolution.y * 100.0/iResolution.x + 0.001;

    float circle = smoothstep(dist + blurness, dist - blurness, length(uv - pos));

    float floorDist = abs(uv.y);
    vec4 floorMask = vec4(vec3(floorDist), 1.0);

    // Time varying pixel color
    vec4 timeCol = 0.5 + 0.5*cos(iTime+uv.xyxy+vec4(0,2,4, 0));

    vec4 circleMask = mix(vec4(floorDist), vec4(timeCol), circle);

    // Output to screen
    fragColor = circleMask;
}
