void mainImage( out vec4 o, in vec2 I )
{
    vec2 a = (2.*I.xy - iResolution.xy) / iResolution.y + vec2(.7,0.);
    float ct = cos(iTime);
  	for (int i = 0; i < 14; i++)
  	{    
   		a = vec2(a.x*a.x-a.y*ct*a.y,a.x*a.y*2.)-1.5*a;
    	a *= step(length(a), 126.);
  	}
  	o = vec4(a*ct, a*sin(iTime));
}
