void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);

    vec3 p0 = vec3(1.0, 0.0, 0.0);
    vec3 p1 = vec3(0.0, 1.0, 0.0);
    vec3 p2 = vec3(0.0, 0.0, 1.0);
    float t = 1.0 - uv.x - uv.y;
    t = abs(cos(t + sin(iTime) + iTime * 0.1));
    vec3 colour = (uv.x * p0) + (uv.y * p1) + (t * p2);
	fragColor = vec4(colour.xyz, 1.0);
}
