void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t=iTime;




    for(float i=1.0;i>0.0;i-=0.01){//step


    if( uv.y>i*sin(i)*4.)          uv.x =uv.x+0.10*sin(t/2.0)/8.;
   // if( uv.y<-i+sin(t*1.5)*0.2)  uv.x =uv.x+0.08*sin(t/2.0)/10.;    



    }    
       vec3 tc=texture(iChannel0,uv/1.1).xyz-.3 ;
       vec3 tc2=texture(iChannel1,uv-vec2(0.0,0.01)).xyz ;





	fragColor = 1.*sign(mod(t,2.0))*vec4(mix(tc*2.0,tc2 ,-0.2),1.0);
}
