float dfBox(vec3 v, float i) {
	return length(v)-abs(sin(iDate.w+dot(vec3(1,1,1),abs(normalize(v)))))*i;
}
vec3 ray(vec3 ro, vec3 rd) {
    vec3 v=ro;
    for (float i = 0.; i < 64.; i++) {

        float d = dfBox(v,2.);
        v+=rd*d;
        if (d<.01)
        {

        	return vec3(texture(iChannel0,reflect(rd,v)));   
        }
    }
    return vec3(texture(iChannel0,rd));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(ray(vec3(0, 0, -10), vec3(uv-.5, 1)),1.0);
}
