#define PI 3.14159265359

float plot(vec2 st, float pct){
  return  smoothstep( pct-(0.05+sin(iTime)/10.0), pct, st.y) - 
          smoothstep( pct, pct+(0.05+sin(iTime)/10.0), st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(st.y * sin(iTime));

    float v = (cos((st.x * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.1;
    float v2 = v +  sin((st.x * 20.0)*100.0*3.1415 + iTime * 1.0) / 10.0  ;
    float v3 = (cos((st.x*sin(st.y) * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.5;

    float pct = plot(st+vec2(0., -0.3) ,v3);
    float pct2 = plot(st ,v2);
	float pct3 = plot(st+vec2(0., 0.2) ,v);

    vec3 bg = vec3(0., 1., 0.);
    vec3 clr = vec3(pct+pct2+pct3);

    color = (clr*color+clr)*vec3(1.0,0.0,0.0);
	color += bg;
	fragColor = vec4(color,1.0);
}
